mapper.update:
update projects set
name = #{name}, charge_user_id = #{chargeUserId}, code_line_number = #{codeLineNumber}, code_update_count = #{codeUpdateCount}, schedule = #{schedule}, function_points = #{functionPoints}, completed_function_points = #{completedFunctionPoints}, last_update_time = #{lastUpdateTime}, type = #{type}, characterization = #{characterization}, store_count = #{storeCount}, like_count = #{likeCount}, click_count = #{clickCount}, user_count = #{userCount}, document_count = #{documentCount},
where id = #{id}

mapper.select:
select count(*)
from projects a,sys_user b
where a.charge_user_id=b.id and a.enabled = 1 and b.enabled=1;

select count(*)
from projects a,sys_user b
where a.charge_user_id=b.id and a.type=#{type} and a.enabled = 1 and b.enabled=1;
