mapper.update:
update projects_user set
projects_id = #{projectsId}, user_id = #{userId}, duty_code = #{dutyCode}, code_devote_line = #{codeDevoteLine}, code_update = #{codeUpdate},
where id = #{id}

mapper.select:
select count(*)
from projects_user
where delete_flag = 1 and projects_id = #{projectId}
